package IntegratedControlSystem {
    import StructuralDefinitions::*;
    import BehavioralDefinitions::*;

    doc /* Merged control system with integrated structure and behavior */

    //
    // 1. Enhanced Controller: bundles structural definition, behavior, and state exposure
    //
    part def EnhancedController :> Controller {
        perform controlLoop : ControlLoop;
        exhibit states      : SystemStates;
    }

    //
    // 2. Integrated System Assembly: brings sensor, controller, actuator together
    //
    part integratedSystem :> system {
        // Controller with explicit control cycle time
        part controller : EnhancedController {
            :>> cycleTime = 0.1;  // 100 ms control cycle
        }

        // Re‑use existing Sensor and Actuator definitions
        part sensor   :> Sensor;
        part actuator :> Actuator;

        // Physical/interface connections (ports)
