-        connection powerFlow connect controller.controllerOutput to motor.motorPower;
-    }
-}
+package ModernPatternVehicle {
+
+    interface def PowerInterface {
+        flow power : Real;
+    }
+
+    port def PowerPort {
+        interface PowerInterface;
+    }
+
+    part def Battery {
+        attribute voltage : Real = 12;
+        attribute capacity : Real = 100;
+        port supply : PowerPort;
+    }
+
+    part def ElectricMotor {
+        attribute ratedPower : Real = 50;
+        port motorPower : PowerPort[conjugate];
+    }
+
+    part def PowerController {
+        attribute outputPower : Real = 75;
+        port controllerPower : PowerPort[conjugate];
+    }
+
+    part vehicle {
+        part battery : Battery {
+            redefines attribute voltage = 24;
+            redefines attribute capacity = 200;
+        }
+
+        part controller : PowerController;
+        part motor : ElectricMotor;
+
+        bind battery.supply to controller.controllerPower;
+        bind controller.controllerPower to motor.motorPower;
+    }
+}

