        attribute has_display_separator : ScalarValues::Boolean;
        attribute has_edit_separator : ScalarValues::Boolean;

        asert constraint { base_format.id == 1 }  // ERROR: "asert" should be "assert"
    }

    part Boolean : Datatype { 
