package HVACSystemVerification {

    import HVACSystemRequirements::*;
    import eVehicleLibrary::*;

    testCase def TemperatureRegulationTestCase {
        doc /* Verify that the HVAC system regulates the cabin temperature within ±1°C of the user’s setpoint. */;
        verify requirement TemperatureRegulationReqDef;
        parameters {
            setTemperature       : Temperature;
            actualTemperature    : Temperature;
        }
        expected {
            abs(actualTemperature - setTemperature) <= 1;
        }
    }

    testCase def CoolingFunctionTestCase {
        doc /* Verify that the HVAC system cools the cabin to at or below the set cooling temperature. */;
        verify requirement CoolingFunctionReqDef;
        parameters {
            setCoolingTemperature    : Temperature;
            actualCoolingTemperature : Temperature;
        }
        expected {
            actualCoolingTemperature <= setCoolingTemperature;
        }
    }

    testCase def HeatingFunctionTestCase {
        doc /* Verify that the HVAC system heats the cabin to at or above the set heating temperature. */;
        verify requirement HeatingFunctionReqDef;
        parameters {
            setHeatingTemperature    : Temperature;
            actualHeatingTemperature : Temperature;
        }
        expected {
            actualHeatingTemperature >= setHeatingTemperature;
        }
    }

    testCase def DefrostingTestCase {
        doc /* Verify that windshield/window defrosting completes within the allowed maximum defrost time. */;
        verify requirement DefrostingReqDef;
        parameters {
            defrostTime     : Time;
            maxDefrostTime  : Time;
        }
        expected {
            defrostTime <= maxDefrostTime;
        }
    }

    testCase def AirQualityControlTestCase {
        doc /* Verify that the HVAC system maintains particle filtration efficiency and humidity within specified bounds. */;
        verify requirement AirQualityControlReqDef;
        parameters {
            particleFiltrationEfficiency : Percentage;
            humidityLevel               : Percentage;
